import React, { Component } from 'react';
import { Route, Link, Switch } from 'react-router-dom';
import Login from '../src/pages/AUTH/Login';
import Signup from '../src/pages/AUTH/Signup';
import Nav from './components/Nav/Nav';
import Contact from './pages/Contact/contact';
import Quotes from './pages/Motivate/quotes';
import Profile from './pages/Profile';
import Addquote from './pages/Motivate/addquote'
import AUTH from './utils/AUTH';
import { Footer, Header, Navigation, Drawer, Layout} from 'react-mdl';


class App1 extends Component {
  
  constructor() {
    super();
    
		this.state = {
			loggedIn: false,
			user: null
    };
  }
  
	componentDidMount() {
		AUTH.getUser().then(response => {
			console.log(response.data);
			if (!!response.data.user) {
				this.setState({
					loggedIn: true,
					user: response.data.user
				});
			} else {
				this.setState({
					loggedIn: false,
					user: null
				});
			}
		});
	}

	logout = (event) => {
    event.preventDefault();
    
		AUTH.logout().then(response => {
			console.log('successfully logged out!');
			console.log(response.status);
			if (response.status === 200) {
				this.setState({
					loggedIn: false,
					user: null
				});
			}

		});
	}

	login = (username, password) => {
		AUTH.login(username, password).then(response => {
      console.log(response);
      if (response.status === 200) {
        // update the state
        this.setState({
          loggedIn: true,
          user: response.data.user
        });
      }
    });
	}

	render() {
		return (
			<Layout>
				<Header className="header-color" title="Motivate Me" scroll>

{/* <Nav/> */}
  <Navigation>
  <Link to="/">Home</Link>
  <Link to="/profile">Profile</Link>
  <Link to="/quotes">My Quotes</Link>
  </Navigation>
</Header>
<Drawer className="drawer-color" title="Motivate Me">
  <Navigation>
	  <Link to="/">Home</Link>
	  <Link to="/profile">Profile</Link>
	  <Link to="/quotes">My Quotes</Link>
  </Navigation>
</Drawer>
				
			<div className="App1">
        { this.state.loggedIn && (
          <div>
			  
            <Nav user={this.state.user} logout={this.logout} />
            
							<Switch>
                				<Route exact path="/" component={() => <Login user={this.state.user}/>} />
                				<Route exact path="/profile" component={() => <Profile user={this.state.user} {...props}/>} />
								<Route exact path="/addquote" component={(props) => <Addquote user={this.state.user}/>} />
								<Route exact path="/quotes" component={Quotes => <Quotes user={this.state.user}/>} />
                                <Route path="/contact" component={Contact} />
              				</Switch>
    
          </div>
        )}
        { !this.state.loggedIn && (
          <div>
            <Route exact path="/" component={() => <Login login={this.login}/>} />
            <Route exact path="/profile" component={() => <Profile login={this.login}/>} />
            <Route exact path="/signup" component={Signup} />
          </div>
        )}
      
			</div>
		
			<div className="footer-links">
				<Footer className="footer-color">
				<Link to="/contact"  style={{ color: "white", textDecoration: 'none' }}>About the Developers</Link>
				</Footer>
			</div>
			</Layout>
		)
	}
}

export default App1;
