import React, { Component } from "react";
import Motivate from './motivate';
import '../../App.css';
import { Grid, Cell, List, ListItem } from 'react-mdl';
import API from "../../utils/API";
import DeleteBtn from "../../components/DeleteBtn";

class NewGoal extends Component {
  state = {
    title: "",
    details: "",
    username: ""
  };
 
  componentDidMount() {
    this.loadGoals();
  }

  loadGoals = () => {
    API.getGoals(this.props.user.username)
      .then(res =>
        this.setState({ goals: res.data, title: "", details: "" })
      )
      .catch(err => console.log(err));
  };

  deleteGoal = id => {
    API.deleteGoals(id)
      .then(res => this.loadGoals())
      .catch(err => console.log(err));
  };

  handleInputChange = event => {
    const { name, value } = event.target;
    this.setState({
      [name]: value
    });
  };

  handleFormSubmit = event => {
    event.preventDefault();

    if (this.state.title && this.state.details ) {
      console.log(this.props)
      API.saveGoals({
        title: this.state.title, 
        details: this.state.details,
        username: this.props.user.username
      })
        .then(res => this.loadGoals())
        .catch(err => console.log(err));
    }
  };
 
  render() {
    return (
        <div>
                <Grid>
                    <Cell col={6}>
                    <div className="profileCard"> 
                         <form className="box motivate" action="/profile" method="post">
                            <h1 className="goaltitle">My New Goal</h1>

                            <input className="input-text" 
                                type="text" 
                                name="title" 
                                value={this.state.title}
                                onChange={this.handleInputChange}
                                placeholder="Title of your new goal">
                            </input>

                                <br></br>

                            <textarea className="textarea" 
                                value={this.state.details}
                                onChange={this.handleInputChange}
                                type="text" 
                                name="details"
                                placeholder="List your details">
                             </textarea>

                                <br></br>

                            <input className="input-text" 
                                type="submit"
                                value="Submit New Goal"
                                disabled={!(this.state.title)}
                                onClick={this.handleFormSubmit}
                            ></input>

                                <br></br>
                        </form>
                    </div>
                    </Cell>
                         
                    <Cell col={6}> 
                    <div> 
                        <h3 align="center" className="DevName">Saved Goals</h3>
                        <hr></hr>
                       
                        <h3 align="center">No Goals to Display</h3>

                    </div> 
                    </Cell>

                </Grid>
            </div>
     
        );
    }
}

export default NewGoal;
