import React, { Fragment } from "react";
import { Link } from 'react-router-dom';
import { Layout, Header, Navigation, Footer, Drawer, Content } from 'react-mdl';


const Nav = (props) => {
  let greeting;

  if (props.user === null) {
		greeting = <p>Hello Guest</p>
	} else if (props.user.firstName) {
		greeting = (
			<Fragment>
				Welcome, <strong>{props.user.firstName}</strong>
			</Fragment>
		)
	} else if (props.user.username) {
		greeting = (
			<Fragment>
				Welcome, <strong>{props.user.username} </strong>
			</Fragment>
		)
  }

  return (
    <div>
   <Header className="header-color" title="Motivate Me" scroll>

  <Navigation>
  {/* <Link to="/">Home</Link> */}
  <Link to="/profile">Profile</Link>
  <Link to="/quotes">My Quotes</Link>
      <div className="float-right">
            {greeting}&emsp;&emsp;  
          <a href="/" className="logout" onClick={props.logout}>Logout</a>
      </div>
  </Navigation>
</Header>
<Drawer className="drawer-color" title="Motivate Me">
  <Navigation>
      {/* <Link to="/">Home</Link> */}
      <Link to="/profile">Profile</Link>
      <Link to="/quotes">My Quotes</Link>
  </Navigation>
</Drawer>



    </div>
  )
};

export default Nav;
