import React, { Component } from 'react';
import { Route, Switch } from 'react-router-dom';
import Login from '../src/pages/AUTH/Login';
import Signup from './src/pages/AUTH/Signup';
import Nav from './src/components/Nav/Nav';
import Contact from './pages/Contact/contact';
import Quotes from './pages/Motivate/quotes';
import Profile from './pages/Profile';
import Addquote from './pages/Motivate/addquote'
import AUTH from './utils/AUTH';


class App1 extends Component {
  
  constructor() {
    super();
    
		this.state = {
			loggedIn: false,
			user: null
    };
  }
  
	componentDidMount() {
		AUTH.getUser().then(response => {
			console.log(response.data);
			if (!!response.data.user) {
				this.setState({
					loggedIn: true,
					user: response.data.user
				});
			} else {
				this.setState({
					loggedIn: false,
					user: null
				});
			}
		});
	}

	logout = (event) => {
    event.preventDefault();
    
		AUTH.logout().then(response => {
			console.log('successfully logged out!');
			console.log(response.status);
			if (response.status === 200) {
				this.setState({
					loggedIn: false,
					user: null
				});
			}

		});
	}

	login = (username, password) => {
		AUTH.login(username, password).then(response => {
      console.log(response);
      if (response.status === 200) {
        // update the state
        this.setState({
          loggedIn: true,
          user: response.data.user
        });
      }
    });
	}

	render() {
		return (
			<div className="App1">
        { this.state.loggedIn && (
          <div>
            <Nav user={this.state.user} logout={this.logout}/>
            <div className="main-view">
							<Switch>
                <Route exact path="/" component={() => <Login user={this.state.user}/>} />
                <Route exact path="/profile" component={(props) => <Profile user={this.state.user} {...props}/>} />
								<Route exact path="/addquote" component={() => <Addquote user={this.state.user}/>} />
								<Route exact path="/quotes" component={() => <Quotes user={this.state.user}/>} />
                                <Route path="/contact" component={Contact} />

              </Switch>
            </div>
          </div>
        )}
        { !this.state.loggedIn && (
          <div>
            <Route exact path="/" component={() => <Login login={this.login}/>} />
            <Route exact path="/profile" component={() => <Login login={this.login}/>} />
            <Route exact path="/signup" component={Signup} />
            <Route path="/contact" component={Contact} />
            
          </div>
        )}
			</div>
		)
	}
}

export default App1;
