import React, { Component } from "react";
import '../App.css';
import { Grid, Cell, List, ListItem, Checkbox } from 'react-mdl';
import API from "../utils/API";
import DeleteBtn from "../components/DeleteBtn";

class DynaGoal extends Component {
    state = {
        title: "",
        details: "",
        username: ""
      };
     
      componentDidMount() {
        this.loadGoals();
      }
    
      loadGoals = () => {
        console.log(this.props.user.username)
        API.getGoals(this.props.user.username)
          .then(res => {
            console.log(res); 
            this.setState({ Goal: res.data, title: "", details: "" })
          })
          .catch (err => console.log(err));
      };
    
    
      deleteGoal = id => {
        API.deleteGoal(id)
          .then(res => this.loadGoals())
          .catch(err => console.log(err));
      };
    
      handleInputChange = event => {
        const { name, value } = event.target;
        this.setState({
          [name]: value
        });
      };
    
      handleFormSubmit = event => {
        event.preventDefault();
    
        if (this.state.title && this.state.details ) {
          console.log(this.props)
          API.saveGoal({
            title: this.state.title, 
            details: this.state.details,
            username: this.props.user.username
          })
            .then(res => this.loadGoals())
            .catch(err => console.log(err));
        }
      };
    render() {
        return (

<Cell col={6}> 

                    <div className="goalsList"> 
                        <h3 align="center" className="DevName">Saved Goals</h3>
                        <hr></hr>
                        {this.state.Goal ? (
                  <List>
                    { this.state.Goal && this.state.Goal.map(Goal => {
                      return (
                        <ListItem key={Goal._id}>
                          <a href={"/goals/" + Goal._id}>
                            <strong>
                              {Goal.title}
                            </strong>
                          </a>
                          &nbsp;
                          <DeleteBtn onClick={() => this.deleteGoal(Goal._id)} />
                        </ListItem>
                      );
                    })}
                  </List>
                ) : (
                  <h3>No Goals to Display</h3>
                )}
                    </div> 
                    </Cell>

        );
    }
}

export default DynaGoal;
